package svkpcorejava;

public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int	x=6;
		if(x==6)
		{
			if(x>=6)
			{
				System.out.println("x is greater than 6");
			}
				else
				{
					System.out.println("x is smaller than 6");
				}
			}
		}

	}


