package svkpcorejava;

public class Switchstring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="vasantha";
		switch(name)
		{
		case "author":
			System.out.println("Mounika");
			break;
		case "team":
			System.out.println("full stack java");
			break;
		case "editor":
			System.out.println("vishnu & krishna");
			break;
		default :
			System.out.println("invalid entry");
			break;
	}

}
}
